import React from 'react';
import { Button, Dialog, DialogTitle, DialogContent, DialogActions } from 'react-mdl';
import { Link } from 'react-router-dom';
import Iframe from 'react-iframe';
import IdleTimer from 'react-idle-timer';
import { reset } from 'ansi-colors';

const APUSupportLink = "https://static.zdassets.com/web_widget/latest/liveChat.html?v=10#key=apu.zendesk.com&&&locale=en-US&title=Web%20Widget%20Live%20Chat";

export class APUSupport extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            // timeTillRestart: 5,
            currentTime: 0,
            openDialog: false
        }
        this.idleTimer = null
        this.onAction = this._onAction.bind(this)
        this.onActive = this._onActive.bind(this)
        this.onIdle = this._onIdle.bind(this)
        this.handleOpenDialog = this.handleOpenDialog.bind(this);
        this.handleCloseDialog = this.handleCloseDialog.bind(this);
    }

    // componentDidMount () {
    //     this.setState(state => ({
    //         remainingSeconds: parseInt(state.selectedMinutes, 10) * 60
    //       }));

    //     this.interval = setInterval(() => {
    //         this.setState(state => ({
    //           remainingSeconds: state.remainingSeconds - 1
    //         }));
    //       }, 240000);
    // }

    handleOpenDialog() {
        this.setState({
        openDialog: true
    });
    }

    handleCloseDialog() {
        this.setState({
            openDialog: false
        });
        this.idleTimer.reset();
    }

    getCurrentTime = () => {
        this.setState({
            currentTime: this.idleTimer.getRemainingTime()
        });
        return this.state.currentTime
    }

    render() {
        return (
            <div>

                <IdleTimer
                    ref={ref => { this.idleTimer = ref }}
                    element={document}
                    onActive={this.onActive}
                    onIdle={this.handleOpenDialog}
                    onAction={this.onAction}
                    timeout={1000 * 60 * 1} />

                <h1>APU Support Chat Page</h1>

                <Iframe url={APUSupportLink}
                    width="600px"
                    height="750px"/>

                <Link to="/">
                    <Button raised>Home</Button>
                </Link>
                <Dialog open={this.state.openDialog} onCancel={this.handleCloseDialog}>
                    <DialogTitle>This Page Will Automaticaly Reset</DialogTitle>
                    <DialogContent>
                        <p>This page will restart in {this.getCurrentTime}</p>
                    </DialogContent>
                    <DialogActions>
                        <Button type='button' onClick={this.handleCloseDialog}>Continue?</Button>
                    </DialogActions>
                </Dialog>
            </div>
        )
    }

    _onAction(e) {
        console.log('user did something', e)
}

    _onActive(e) {
        console.log('user is active', e)
        console.log('time remaining', this.idleTimer.getRemainingTime())
    }

    _onIdle(e) {
        console.log('user is idle', e)
        console.log('last active', this.idleTimer.getLastActiveTime())
    }
}

export default APUSupport;
