import React from "react";
import { Button } from "react-mdl";
import { Link } from "react-router-dom";
import IdleTimer from "react-idle-timer";
import { IdleTimeOutModal } from "../components/IdleModal";
import "./home.css";

// URL for iframe
const WEPASupportPage = "https://support.wepanow.com/";

//////////////////////////////////////////////////////
/* Styles                                           */
//////////////////////////////////////////////////////
const iframeStyle = {
  marginTop: "60px"
};
const leftDiv = {
  flex: 1,
  width: "50vw",
  height: "94vh"
};
const rightDiv = {
  flex: 1
};
const buttonStyle = {
  marginTop: 15
};
const leftText = {
  marginTop: "30vh",
  textAlign: "center",
  fontSize: 56,
  fontWeight: "bold"
};
const paragraphStyle = {
  marginTop: 25,
  marginLeft: 150,
  marginRight: 150,
  textAlign: "center",
  fontSize: 24,
  color: "#808080"
};
//////////////////////////////////////////////////////

//////////////////////////////////////////////////////
/* Wepa Support Page                                */
//////////////////////////////////////////////////////
export class WEPASupport extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      // Minutes before timeout
      timeIdle: 5,
      showModal: false,
      userLoggedIn: false,
      isTimedOut: false,
      isGettingOff: false
    };
    this.idleTimer = null;
    this.onAction = this._onAction.bind(this);
    this.onActive = this._onActive.bind(this);
    this.onIdle = this._onIdle.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.handleLogout = this.handleLogout.bind(this);
  }

  handleClose() {
    this.setState({ showModal: false, timeIdle: 3 });
  }

  handleLogout() {
    this.setState({ showModal: false, isGettingOff: true });
    this.props.history.push("/");
  }

  // Display
  displayModal() {
    if (this.state.showModal) {
      return (
        <IdleTimeOutModal
          showModal={this.state.showModal}
          handleClose={this.handleClose}
          handleLogout={this.handleLogout}
        />
      );
    } else {
      return null;
    }
  }

  runTimer() {
    return (
      <IdleTimer
        ref={ref => {
          this.idleTimer = ref;
        }}
        element={document}
        onActive={this.onActive}
        onIdle={this.onIdle}
        onAction={this.onAction}
        debounce={250}
        timeout={1000 * 60 * this.state.timeIdle}
      />
    );
  }

  addEvents() {
    const frame = document.getElementById("wepaWidget");
    frame.componentWindow.addEventListener("mosemove", this.onAction);
  }

  render() {
    return (
      <div className="flexRow" id="wepaPage">
        {this.runTimer()}
        <div style={leftDiv}>
          <h1 style={leftText}>WEPA Support</h1>
          <p style={paragraphStyle}>
            This is WEPA's support page. Please feel free to use the live chat
            to connect directly to a WEPA support representative. If the live
            chat is not available, please search for the problem using the
            search bar. If you still need assistance please try the APU Support
            Chat or call (626)855-5050.
          </p>
          <p style={{ textAlign: "center", marginTop: 25 }}>
            This page will timeout after 5 minutes of inactivity.
          </p>
          <Link to="/">
            <Button raised className="centered" style={buttonStyle}>
              Home
            </Button>
          </Link>
        </div>
        <div style={rightDiv}>
          <iframe
            id="wepaWidget"
            title="wepaWidget"
            src={WEPASupportPage}
            width="100%"
            height="100%"
            style={{ iframeStyle }}
          />
        </div>
        {this.displayModal()}
      </div>
    );
  }

  _onAction(e) {
    if (!this.isGettingOff) {
      console.log("user did something", e);
      // console.log('time remaining', this.idleTimer.getRemainingTime())

      this.setState({ isTimedOut: false });
    }
  }

  _onActive(e) {
    console.log("user is active", e);
    if (!this.showModal) {
      this.setState({ isTimedOut: false });
    }
  }

  _onIdle(e) {
    console.log("user is idle", e);
    const isTimedOut = this.state.isTimedOut;
    if (isTimedOut) {
      this.props.history.push("/");
    } else {
      this.setState({ showModal: true });
      this.setState({ timeIdle: 1 });
      this.idleTimer.reset();
      console.log("time remaining", this.idleTimer.getRemainingTime());
      this.setState({ isTimedOut: true });
    }
  }
}

export default WEPASupport;
