import React from "react";
import { Button } from "react-mdl";
import { Link } from "react-router-dom";
import IdleTimer from "react-idle-timer";
import { IdleTimeOutModal } from "../components/IdleModal";
import "./home.css";

const APUSupportLink =
  "https://static.zdassets.com/web_widget/latest/liveChat.html?v=10#key=apu.zendesk.com&&&locale=en-US&title=Web%20Widget%20Live%20Chat";

const iframeStyle = {
  marginTop: "40px",
  marginLeft: "40px"
};
const buttonStyle = {
  marginTop: 10
};

export class APUSupport extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      timeIdle: 5,
      currentTime: 0,
      showModal: false,
      userLoggedIn: false,
      isTimedOut: false,
      isGettingOff: false
    };
    this.idleTimer = null;
    this.onAction = this._onAction.bind(this);
    this.onActive = this._onActive.bind(this);
    this.onIdle = this._onIdle.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.handleLogout = this.handleLogout.bind(this);
  }

  handleClose() {
    this.setState({ showModal: false, timeIdle: 3 });
  }

  handleLogout() {
    this.setState({ showModal: false, isGettingOff: true });
    this.props.history.push("/");
  }

  // Display
  displayModal() {
    if (this.state.showModal) {
      return (
        <IdleTimeOutModal
          showModal={this.state.showModal}
          handleClose={this.handleClose}
          handleLogout={this.handleLogout}
        />
      );
    } else {
      return null;
    }
  }

  runTimer() {
    return (
      <IdleTimer
        ref={ref => {
          this.idleTimer = ref;
        }}
        element={document}
        onActive={this.onActive}
        onIdle={this.onIdle}
        onAction={this.onAction}
        debounce={250}
        timeout={1000 * 60 * this.state.timeIdle}
      />
    );
  }

  addEvents() {
    const frame = document.getElementById("apuWidget");
    frame.componentWindow.addEventListener("mosemove", this.onAction);
  }

  render() {
    return (
      <div className="flexColumn" id="apuPage">
        {this.runTimer()}
        <div>
          <h1 style={{ textAlign: "center" }}>APU Support Chat</h1>
          <p style={{ textAlign: "center" }}>
            This chat connects you directly to a APU support representative that
            can answer any questions you may have concerning lab computers or
            account issues.
          </p>
          <p style={{ textAlign: "center" }}>
            This page will refresh after 5 minutes of inactivity.
          </p>
          <Link to="/">
            <Button raised className="centered" style={buttonStyle}>
              Home
            </Button>
          </Link>
        </div>
        <div>
          <iframe
            id="apuWidget"
            title="apuWidget"
            src={APUSupportLink}
            width="100%"
            height="700px"
            style={{ iframeStyle }}
          />
        </div>
        {this.displayModal()}
      </div>
    );
  }

  _onAction(e) {
    if (!this.isGettingOff) {
      console.log("user did something", e);
      // console.log('time remaining', this.idleTimer.getRemainingTime())

      this.setState({ isTimedOut: false });
    }
  }

  _onActive(e) {
    console.log("user is active", e);
    if (!this.showModal) {
      this.setState({ isTimedOut: false });
    }
  }

  _onIdle(e) {
    console.log("user is idle", e);
    const isTimedOut = this.state.isTimedOut;
    if (isTimedOut) {
      this.props.history.push("/");
    } else {
      this.setState({ showModal: true });
      this.setState({ timeIdle: 1 });
      this.idleTimer.reset();
      console.log("time remaining", this.idleTimer.getRemainingTime());
      this.setState({ isTimedOut: true });
    }
  }
}

export default APUSupport;
