import React from "react";
import { Button } from "react-mdl";
import { Link } from "react-router-dom";
import IdleTimer from "react-idle-timer";
import { IdleTimeOutModal } from "../components/IdleModal";
import "./home.css";

const WEPALink =
  "https://wepa.ladesk.com/scripts/generateWidget.php?v=5.15.10.5&t=1571154394&cwid=b8b00bd3&cwt=chat_popout&cid=N14O0RCiYJF409sy&vid=4rruri2tzhwq9do9sru159rjec52i";
const WEPASupportPage = "https://support.wepanow.com/";

const iframeStyle = {
  marginTop: "40px",
  marginLeft: "40px"
};
const leftDiv = {
  flex: 1,
  backgroundColor: "#1ecbe1",
  width: "50%",
  height: "94vh"
};
const rightDiv = {
  flex: 1
};
const buttonStyle = {
  marginTop: 15
};
const leftText = {
  marginTop: "38%",
  textAlign: "center"
};

export class WEPASupport extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      timeIdle: 5,
      currentTime: 0,
      showModal: false,
      userLoggedIn: false,
      isTimedOut: false,
      isGettingOff: false
    };
    this.idleTimer = null;
    this.onAction = this._onAction.bind(this);
    this.onActive = this._onActive.bind(this);
    this.onIdle = this._onIdle.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.handleLogout = this.handleLogout.bind(this);
  }

  handleClose() {
    this.setState({ showModal: false, timeIdle: 3 });
  }

  handleLogout() {
    this.setState({ showModal: false, isGettingOff: true });
    this.props.history.push("/");
  }

  // Display
  displayModal() {
    if (this.state.showModal) {
      return (
        <IdleTimeOutModal
          showModal={this.state.showModal}
          handleClose={this.handleClose}
          handleLogout={this.handleLogout}
        />
      );
    } else {
      return null;
    }
  }

  runTimer() {
    return (
      <IdleTimer
        ref={ref => {
          this.idleTimer = ref;
        }}
        element={document}
        onActive={this.onActive}
        onIdle={this.onIdle}
        onAction={this.onAction}
        debounce={250}
        timeout={1000 * 60 * this.state.timeIdle}
      />
    );
  }

  addEvents() {
    const frame = document.getElementById("wepaWidget");
    frame.componentWindow.addEventListener("mosemove", this.onAction);
  }

  render() {
    return (
      <div className="flexRow" id="wepaPage">
        {this.runTimer()}
        <div style={leftDiv}>
          <h1 style={leftText}>WEPA Support</h1>
          <p style={{ textAlign: "center" }}>
            This chat connects you directly to a APU support representative that
            can answer any questions you may have concerning lab computers or
            account issues.
          </p>
          <p style={{ textAlign: "center" }}>
            This page will refresh after 5 minutes of inactivity.
          </p>
          <Link to="/">
            <Button raised className="centered" style={buttonStyle}>
              Home
            </Button>
          </Link>
        </div>
        <div style={rightDiv}>
          <iframe
            id="wepaWidget"
            title="wepaWidget"
            src={WEPASupportPage}
            width="100%"
            height="100%"
            style={{ iframeStyle }}
          />
        </div>
        {this.displayModal()}
      </div>
    );
  }

  _onAction(e) {
    if (!this.isGettingOff) {
      console.log("user did something", e);
      // console.log('time remaining', this.idleTimer.getRemainingTime())

      this.setState({ isTimedOut: false });
    }
  }

  _onActive(e) {
    console.log("user is active", e);
    if (!this.showModal) {
      this.setState({ isTimedOut: false });
    }
  }

  _onIdle(e) {
    console.log("user is idle", e);
    const isTimedOut = this.state.isTimedOut;
    if (isTimedOut) {
      this.props.history.push("/");
    } else {
      this.setState({ showModal: true });
      this.setState({ timeIdle: 1 });
      this.idleTimer.reset();
      console.log("time remaining", this.idleTimer.getRemainingTime());
      this.setState({ isTimedOut: true });
    }
  }
}

export default WEPASupport;
